INSERT INTO prepper.comptia_cloud_plus_questions (question_number, category, difficulty, domain, question_text, options, correct_answer, explanation, explanation_details, multiple_answers, correct_answers, question_id, id) OVERRIDING SYSTEM VALUE VALUES (334, 'Cloud Architecture - Service Models', 'Comprehension', 'Cloud Architecture and Models', 'Your company wants to deploy a web application without managing the underlying servers, operating systems, or runtime environments. Which service model best fits this requirement?', '[{"text": "Infrastructure as a Service (IaaS)", "isCorrect": false}, {"text": "C) Software as a Service (SaaS)", "isCorrect": false}, {"text": "B) Platform as a Service (PaaS)", "isCorrect": true}, {"text": "D) Function as a Service (FaaS)", "isCorrect": false}]', 'B) Platform as a Service (PaaS)', 'PaaS abstracts away infrastructure management while providing a platform for application development and deployment.', '{"summary": "PaaS characteristics for this scenario:", "breakdown": ["Eliminates server and OS management overhead", "Provides development tools and runtime environments", "Allows focus on application code and business logic", "Examples: Azure App Service, Google App Engine, Heroku"], "otherOptions": "A) IaaS requires managing servers and OS\nC) SaaS provides complete applications\nD) FaaS is for event-driven functions"}', NULL, NULL, 216, 216) ON CONFLICT DO NOTHING;
INSERT INTO prepper.comptia_cloud_plus_questions (question_number, category, difficulty, domain, question_text, options, correct_answer, explanation, explanation_details, multiple_answers, correct_answers, question_id, id) OVERRIDING SYSTEM VALUE VALUES (3, 'Cloud Architecture - Service Models', 'Updated Knowledge Level', 'Cloud Architecture and Models', 'Which cloud service model provides virtualized computing resources over the internet, allowing users to rent servers, storage, and networking without purchasing physical hardware?', '[{"text": "Infrastructure as a Service (IaaS)", "isCorrect": true}, {"text": "Platform as a Service (PaaS)", "isCorrect": false}, {"text": "Function as a Service (FaaS)", "isCorrect": false}, {"text": "Software as a Service (SaaS)", "isCorrect": false}]', 'Infrastructure as a Service (IaaS)', 'Updated explanation for testing purposes', '{"summary": "IaaS characteristics include:", "breakdown": ["Virtualized computing resources over the internet", "Users rent infrastructure components rather than buying hardware", "Control over operating systems and applications", "Examples: AWS EC2, Azure VMs, Google Compute Engine"], "otherOptions": "SaaS delivers complete software applications\nPaaS provides development platforms\nFaaS provides serverless function execution"}', NULL, NULL, 1, 1) ON CONFLICT DO NOTHING;
INSERT INTO prepper.comptia_cloud_plus_questions (question_number, category, difficulty, domain, question_text, options, correct_answer, explanation, explanation_details, multiple_answers, correct_answers, question_id, id) OVERRIDING SYSTEM VALUE VALUES (69, 'Cloud Identity Management', 'Application', 'Security', 'A company acquires three subsidiaries, each with different identity providers (AD, Google Workspace, Okta). They need unified cloud access for 5,000 total users while maintaining each subsidiary''s existing identity system. Compliance requires MFA and privileged access management. Which identity architecture best meets these requirements?', '[{"text": "Implement federated identity with SAML/OIDC, centralized MFA, and PAM solution", "isCorrect": true}, {"text": "Create cloud accounts for each subsidiary with separate identity systems", "isCorrect": false}, {"text": "Sync all identities to cloud provider''s native directory service", "isCorrect": false}, {"text": "Migrate all users to a single corporate identity provider", "isCorrect": false}]', 'Implement federated identity with SAML/OIDC, centralized MFA, and PAM solution', 'Federation allows each subsidiary to maintain their identity provider while SAML/OIDC provides secure cloud access. Centralized MFA and PAM ensure consistent security controls.', '{"summary": "Federated identity architecture benefits:", "breakdown": ["SAML/OIDC federation preserves existing identity investments", "Users maintain single credentials (reduced password fatigue)", "Centralized MFA policy applies regardless of source IdP", "PAM solution provides consistent privileged access controls"], "otherOptions": "Migration disrupts 5,000 users and requires retraining\nSeparate accounts prevent unified access and compliance\nSync creates password management and security challenges"}', NULL, NULL, 69, 69) ON CONFLICT DO NOTHING;
INSERT INTO prepper.comptia_cloud_plus_questions (question_number, category, difficulty, domain, question_text, options, correct_answer, explanation, explanation_details, multiple_answers, correct_answers, question_id, id) OVERRIDING SYSTEM VALUE VALUES (147, 'Security', 'Comprehension', 'Security', 'Which of the following are valid authentication factors used in Multi-Factor Authentication (MFA)? (Choose THREE)', '[{"text": "Something you know (e.g., a password)", "isCorrect": true}, {"text": "Something you have (e.g., a hardware token or mobile phone)", "isCorrect": true}, {"text": "Something you are (e.g., a fingerprint or face scan)", "isCorrect": true}, {"text": "Somewhere you are (e.g., a specific IP address)", "isCorrect": false}, {"text": "Something you do (e.g., a specific keystroke pattern)", "isCorrect": false}, {"text": "Something you create (e.g., a new user account)", "isCorrect": false}]', 'Something you know (e.g., a password), Something you have (e.g., a hardware token or mobile phone), Something you are (e.g., a fingerprint or face scan)', 'Multi-Factor Authentication is a security system that requires more than one method of authentication from independent categories of credentials to verify the user''s identity for a login or other transaction. The three standard categories are knowledge, possession, and inherence.', '{"summary": "MFA is based on knowledge, possession, and inherence.", "breakdown": ["Something you know: Password, PIN, security question answer.", "Something you have: Mobile phone (for SMS or authenticator app), USB security key, smart card.", "Something you are: Biometrics like a fingerprint, facial recognition, or iris scan."], "otherOptions": "Location and behavioral patterns are sometimes used as signals in adaptive authentication but are not considered one of the three core MFA factors.\n\nCreating an account is not an authentication factor."}', B'1', '{"Something you know (e.g., a password)","Something you have (e.g., a hardware token or mobile phone)","Something you are (e.g., a fingerprint or face scan)"}', 130, 130) ON CONFLICT DO NOTHING;
INSERT INTO prepper.comptia_cloud_plus_questions (question_number, category, difficulty, domain, question_text, options, correct_answer, explanation, explanation_details, multiple_answers, correct_answers, question_id, id) OVERRIDING SYSTEM VALUE VALUES (12, 'Cloud Architecture - Storage', 'Application', 'Cloud Architecture and Models', 'Your organization needs to store 100TB of archived data that is accessed once per year for compliance purposes. Which storage solution offers the best cost optimization?', '[{"text": "Hot storage tier", "isCorrect": false}, {"text": "Archive storage tier", "isCorrect": true}, {"text": "Cold storage tier", "isCorrect": false}, {"text": "Warm storage tier", "isCorrect": false}]', 'Archive storage tier', 'Archive storage tier is designed for long-term retention of rarely accessed data with lowest cost.', '{"summary": "Archive storage characteristics:", "breakdown": ["Lowest cost storage option", "Designed for long-term retention", "Higher retrieval times and costs", "Ideal for compliance and backup data"], "otherOptions": "Hot storage too expensive for rarely accessed data\nWarm storage still higher cost than needed\nCold storage more expensive than archive"}', NULL, NULL, 10, 10) ON CONFLICT DO NOTHING;
